COUNT =0

def find_uid(input):
    target_c = '\"'
    target_pos = []
    for pos,char in enumerate(input):
        if(char == target_c):
            target_pos.append(pos)
            if(len(target_pos) >= 2):
                break
    uid = input[target_pos[0]+1:target_pos[1]]
    return uid
    

def get_current_time():
    currentDateAndTime = datetime.now()
    t = str(currentDateAndTime)
    t = t.replace(' ','T')
    t = t[0:-3]
    t = t + 'Z'
    return t
    

def replace_uid_time(input,output):
    uid = find_uid(input)
    output = output.replace('ocppuid',uid)
    t = get_current_time()
    output = output.replace('ocppcurrentTime',t)
    return output

def count_time():
    global COUNT
    COUNT +=1
    COUNT &= 3
    return COUNT

async def WebSocketserver(websocket, path):
    while (1):
        #await websocket.send(OCPP_Call_ChangeAvailable)
        Rxdata = await websocket.recv()
        #print(f" Rx from EVSE:\r\n {Rxdata}\r\n")
        # await websocket.send("88:8088 get!")
        if "Authorize" in Rxdata:
            await websocket.send(OCPP_CallResult_authorize)
        elif "BootNotification" in Rxdata:
            tx = replace_uid_time(Rxdata,OCPP_CallResult_boot_r)
            await websocket.send(tx)
        elif "Heartbeat" in Rxdata:
            tx = replace_uid_time(Rxdata,OCPP_CallResult_heart_r)
            await websocket.send(tx)
            count = count_time()
            if count == 2:
                await websocket.send(OCPP_CALL_SetChargingProfile3)
            elif count == 1:
                await websocket.send(OCPP_CALL_SetChargingProfile2)
            else:
                await websocket.send(OCPP_CALL_SetChargingProfile1)
            
            
            

        else:
            print("=========skip========")


start_server = websockets.serve(WebSocketserver, Host, SocketPort,compression=None,ping_interval=5)

